import React, { useEffect, useState,useContext} from 'react';
import { useParams } from 'react-router-dom';
import shopping from '../../images/shopping.jpg';
import {UserContext} from '../../App';
import Footer from '../Footer/Footer';
import CommonNav from '../CommonNav/CommonNav';

const SelectedProduct = () => {
    let { productId } = useParams();
    const [products, setProducts] = useState([]);
    const [loggedinUser, setLoggedinUser] = useContext(UserContext);

    useEffect(() => {
        fetch(`http://localhost:5055/singleProduct/${productId}`)
            .then(res => res.json())
            .then(data => setProducts(data));
    }, [productId])

    console.log(products);

    const uploadSelectedProduct =(products)=>{
        console.log(products);
    }

    return (
        <div className="container">
            <CommonNav></CommonNav>
            <div className="row mt-5">
                <div className="col-md-6">
                    <img src={shopping} style={{ width: '450px', height: '550px' }} alt="" />
                </div>

                <div className="col-md-6 d-flex justify-content-center">

                    <div class="card text-center shadow-lg" style={{ width:'18rem', height: '32rem' }}>
                        <h3 className="text-success">Your selected item</h3>
                        <img class="card-img-top p-3" style={{ height:'250px'}} src={products[0]?.imageURL} alt="product"/>
                            <div class="card-body">
                                <h3 class="card-title text-success">{products[0]?.name}</h3>
                                <p class="card-text">Wight:{products[0]?.weight}</p>
                                <p class="card-text">Price:{products[0]?.price}$</p>
                                <button onClick={() =>uploadSelectedProduct(products)} className="buy-button">Proceed</button>
                            </div>
                        </div>

                    </div>

                </div>
                <Footer></Footer>
            </div>
    );
};

export default SelectedProduct;
