import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from "../../App";
import { Nav, Navbar, Container, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Footer from '../Footer/Footer';


const handleLogout = () => {
    console.log('Clicked');
    sessionStorage.removeItem('token');
    sessionStorage.removeItem('email');
}

const ProceedOrder = () => {
    const [order, setOrder] = useState([]);
    const [loggedinUser] = useContext(UserContext);
    //const email = sessionStorage.getItem('email');
    //const [email,setEmail] = useState();
    //console.log(email);
    const email = loggedinUser.email;

    useEffect(() => {
        fetch(`http://localhost:5055/orders/${email}`)
            .then(res => res.json())
            .then(data => setOrder(data));
    }, [email])


    const removeItem = (event,id) => {
        console.log(event);
        fetch(`http://localhost:5055/removeItem/${id}`, {
            method: 'DELETE'
        })
            .then(response => response.json())
            .then(data => {

            })
    }

    return (
        <Container>
            <div className="row">
                <div className="col-md-12">
                    <Navbar bg="light" variant="light">
                        <Navbar.Brand className="text-success" href="/home">Picnic Super Market</Navbar.Brand>
                        <Navbar.Collapse className="justify-content-end">
                            <Nav>
                                <Nav.Link href="/home">Home</Nav.Link>
                            </Nav>
                            <Link to="/home">
                                <Button onClick={handleLogout} className="btn btn-success">Log Out</Button>
                            </Link>
                        </Navbar.Collapse>
                    </Navbar>
                </div>
            </div>


            <div className="row mt-5">
                <div className="col-md-6">
                    <h3> <span className="text-success">{loggedinUser.name}'s</span> Orders are:</h3>

                            {
                                order.map(eachOrder =>
                                <div>
                                    <h3>{eachOrder.name}</h3>
                                     <Button onClick={() => removeItem(eachOrder._id)} className="btn btn-warning">Remove</Button>
                                </div>

                                )
                            }

                </div>

                <div className="col-md-6">
                    <h3>Total Price:</h3>
                </div>
            </div>
            <Footer></Footer>
        </Container>
    );
};

export default ProceedOrder;
