import React, { useState, useContext } from 'react';
import CommonNav from '../CommonNav/CommonNav';
import { Link,useHistory } from 'react-router-dom';
import { UserContext } from '../../App';

const ContinuePage = () => {
    const [isAdmin, setAdmin] = useState(false);
    const [loggedinUser] = useContext(UserContext);
    const history = useHistory();

    const checkAdmin =() => {
        //console.log(loggedinUser);
        const adminMail = loggedinUser.email;
        fetch(`http://localhost:5055/getAdmin/${adminMail}`)
            .then(res => res.json())
            .then(data => {
                if(data){
                   history.replace('/manage');
                }else{
                    console.log(data);
                    setAdmin(true);
                }
            });          
    }

    // const adminAuth=()=> {
    //     console.log(isAdmin);
    //     if(isAdmin){
    //         history.replace('/manage');
    //     }else{
    //         console.log('Nope');
    //     }
    // }

    return (
        <div className="container">
            <CommonNav></CommonNav>
            <div className="row">
                <div className="col-md-6 mt-5 pt-5">
                    {
                        isAdmin ? <h1>You are not an admin but you can still check</h1> : <h1>Nothing</h1>
                    }
                    <button onClick={() => { checkAdmin() }} className="button-design">Continue</button>
                </div>
            </div>
        </div>
    );
};

export default ContinuePage;
