import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import shopping from '../../images/shopping.jpg';
import Footer from '../Footer/Footer';

const SelectedProduct = () => {
    let { productId } = useParams();
    const [products, setProducts] = useState([])

    useEffect(() => {
        fetch(`http://localhost:5055/singleProduct/${productId}`)
            .then(res => res.json())
            .then(data => setProducts(data));
    }, [productId])

    console.log(products);

    return (
        <div className="container">
            <div className="row mt-5 pt-5">
                <div className="col-md-6">
                    <h2>One more step to go...</h2>
                    <img src={shopping} style={{ width: '450px', height: '550px' }} alt="" />
                </div>

                <div className="col-md-6">
                    <h3>Your selected item</h3>

                    <div class="card" style={{ width:'18rem'}}>
                        <img class="card-img-top p-3" style={{ height:'250px'}} src={products[0]?.imageURL} alt="product"/>
                            <div class="card-body">
                                <h5 class="card-title">{products[0]?.name}</h5>
                                <p class="card-text"></p>
                                <p class="card-text"></p>
                                <p class="card-text"></p>
                                <a href="/" class="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>

                    </div>

                </div>
                <Footer></Footer>
            </div>
    );
};

export default SelectedProduct;
