import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from "../../App";
import { Nav, Navbar, Container, Table, Button } from "react-bootstrap";
import { Link} from "react-router-dom";
import Footer from '../Footer/Footer';


const handleLogout = () => {
    console.log('Clicked');
    sessionStorage.removeItem('token');
}

const ProceedOrder = () => {
    const [order, setOrder] = useState([]);
    const [loggedinUser] = useContext(UserContext);

    useEffect(() => {
        fetch(`http://localhost:5055/orders/$`)
            .then(res => res.json())
            .then(data => setOrder(data));
    }, [loggedinUser.email])

    return (
        <Container>
            <div className="row">
                <div className="col-md-12">
                    <Navbar bg="light" variant="light">
                        <Navbar.Brand className="text-success" href="/home">Picnic Super Market</Navbar.Brand>
                        <Navbar.Collapse className="justify-content-end">
                            <Nav>
                                <Nav.Link href="/home">Home</Nav.Link>
                            </Nav>
                            <Link to="/home">
                                <Button onClick={handleLogout} className="btn btn-success">Log Out</Button>
                            </Link>
                        </Navbar.Collapse>
                    </Navbar>
                </div>
            </div>


            <div className="row">
                <div className="col-md-6">
                    <h3>Your Orders:</h3>
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Price($)</th>
                                <th>Quantity</th>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                order.map(eachOrder =>
                                    <tr>
                                        <td>{eachOrder.name}</td>
                                        <td>{eachOrder.price}</td>
                                        <td>{eachOrder.quantity}</td>
                                        <td>
                                            <button className="buy-button">Remove</button>
                                        </td>
                                    </tr>
                                )
                            }
                        </tbody>

                    </Table>
                </div>

                <div className="col-md-6">
                    <h3>Total Price:</h3>
                </div>
            </div>
            <Footer></Footer>
        </Container>
    );
};

export default ProceedOrder;
